// src/components/ChatBox.js
import React, { useState } from 'react';
import { sendMessageToAI } from './utils/GptAPI';

const ChatBox = () => {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');

  const handleSendMessage = async () => {
    if (newMessage.trim()) {
      try {
        const updatedMessages = [...messages, { text: newMessage, user: true }];
        const responseText = await sendMessageToAI(updatedMessages);
        setMessages([...updatedMessages, { text: responseText, user: false }]);
        setNewMessage('');
      } catch (error) {
        // Handle any errors from API call here, e.g., show an error message
      }
    }
  };

  return (
    <div>
      <div className="chat-container">
        {messages.map((message, index) => (
          <div key={index} className={message.user ? 'user-message' : 'ai-message'}>
            {message.text}
          </div>
        ))}
      </div>
      <input
        type="text"
        placeholder="Type your message..."
        value={newMessage}
        onChange={(e) => setNewMessage(e.target.value)}
      />
      <button className="chat-button" onClick={handleSendMessage}>Send</button>
    </div>
  );
};

export default ChatBox;
