import React, { useState, useEffect } from 'react';
import { sendMessageToAI } from './utils/GptAPI';

const ChatBox = () => {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');

  useEffect(() => {
    const initialContext = {
      "role": "system",
      "content": "You are acting as an interviewer for a software engineering role. Please ask a behavioral focused question."
    };
    gptCall(initialContext, true);
  }, []);

  const gptCall = async (newContent, isInitial = false) => {
    try {
      const newMessage = isInitial ? newContent : { "role": "user", "content": newContent };
      const updatedMessages = isInitial ? [newContent] : [...messages, newMessage];
      const responseText = await sendMessageToAI(updatedMessages);

      // Ensure that we use the latest messages state
      setMessages(prevMessages => [...prevMessages, newMessage, { "role": "system", "content": responseText }]);

      if (!isInitial) {
        setNewMessage('');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  }

  const handleSendMessage = () => {
    if (newMessage.trim()) {
      gptCall(newMessage);
    }
  };

  return (
    <div>
      <div className="chat-container">
        {messages.map((message, index) => (
          <div key={index} className={message.role === "user" ? 'user-message' : 'ai-message'}>
            {message.content}
          </div>
        ))}
      </div>
      <input
        type="text"
        placeholder="Type your message..."
        value={newMessage}
        onChange={(e) => setNewMessage(e.target.value)}
      />
      <button className="chat-button" onClick={handleSendMessage}>Send</button>
    </div>
  );
};

export default ChatBox;
